mapper.select:
SELECT PNO, PSUBJECT, POSTERNAME, CRE_DATE, REPOST, PVIEWS, BYUSER FROM (SELECT (ROW_NUMBER() OVER()) AS ROWNUM, PNO, PSUBJECT, POSTERNAME, CRE_DATE, REPOST, PVIEWS, BYUSER FROM ( SELECT (ROW_NUMBER() OVER()) AS ROWNUM, PNO, PSUBJECT, POSTERNAME, CRE_DATE, REPOST, PVIEWS, BYUSER FROM BOARD
ORDER BY REPOST DESC, PNO DESC) AS ROWBOARD ) AS ORDERBOARD
WHERE ROWNUM > (#{pageNum}-1)*#{amount} AND ROWNUM <= #{pageNum}*#{amount}

SELECT PNO, PSUBJECT, PTEXT, POSTERNAME, CRE_DATE, MOD_DATE, REPOST, PVIEWS, BYUSER
FROM BOARD
WHERE PNO=#{postNo}

mapper.update:
UPDATE BOARD SET
                 PSUBJECT = #{postSubject},
                 PTEXT = #{postText},
                 MOD_DATE = now(),
                 BYUSER = #{postUserNo}
WHERE PNO = #{postNo}
