mapper.select:
SELECT (ROW_NUMBER() OVER()) AS ROWNUM, PNO, PSUBJECT, POSTERNAME, CRE_DATE, PVIEWS, REGROUP, REDEPTH, REORDER, ENABLED, CNTREPLY FROM ( -- Paging 처리하기 위한 ROWNUM 추가 SELECT (ROW_NUMBER() OVER()) AS rn, PNO, PSUBJECT, POSTERNAME, CRE_DATE, PVIEWS, REGROUP, REDEPTH, REORDER, ENABLED FROM ( -- 검색어 조건 조회 서브쿼리 SELECT PNO, PSUBJECT, POSTERNAME, CRE_DATE, PVIEWS, REGROUP, REDEPTH, REORDER, ENABLED FROM BOARD WHERE
ENABLED='T' ORDER BY regroup DESC, reorder ASC ) AS SEARCHBOARD ) AS ORDERBOARD LEFT JOIN (SELECT PNO AS PNO2, COUNT(*) AS CNTREPLY FROM REPLY GROUP BY PNO2) AS JOINBOARD ON PNO = PNO2 -- Paging
WHERE rn > (#{pageNum}-1)*#{amount} AND rn <= #{pageNum}*#{amount}

mapper.insert:
BEGIN;
    UPDATE board SET REORDER = REORDER+1 WHERE REGROUP=#{regroup} AND REORDER >= #{reorder};
    INSERT INTO BOARD(PSUBJECT, PTEXT, POSTERNAME, CRE_DATE, MOD_DATE, REGROUP, REDEPTH, REORDER)
    VALUES
        (#{postSubject}, #{postText}, #{posterName},
         NOW(), NOW(), #{regroup}, #{redepth}, #{reorder});
END;
